mapper.select:
select id, user_name, account, password, head_image, state, modifier, modify_time, 
creator, create_time, remark
from t_user


    and user_name like CONCAT('%',#{userName},'%')


    and account = #{account}


    and password = #{password}


    and head_image = #{headImage}


    and state = #{state}

select count(1) from t_user


    and user_name like CONCAT('%',#{userName},'%')


    and account = #{account}


    and password = #{password}


    and head_image = #{headImage}


    and state = #{state}
